import{_,X as t,Y as s,Z as e,$ as o}from"./framework-e45c54a7.js";const n={},c=e("h1",{id:"体验也是按原则做事",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#体验也是按原则做事","aria-hidden":"true"},"#"),o(" 体验也是按原则做事")],-1),a=e("p",null,"Created time: March 11, 2023 9:36 PM",-1),l=e("p",null,"3/90",-1),d=e("p",null,"体验也是按原则做事",-1),r=e("p",null,"前两天去做了个体检，在排队的等号的时候发生了一件事情，有个小姐姐气冲冲地跑到前台质问，扬言要投诉体检中心，“你们到底是排队的还是叫号的，我在那排队那么长时间，也没人说，队伍都排到跟前了，跟我说看屏幕等叫号”。",-1),i=e("p",null,"起初我以为就是一些误会，心里也想着可能这个小姐姐是没搞清楚体检中心排队的规则。可后来切实经历了整个体检过程，我发现，事情没我想象的那么简单，体检中心划分了几个区域，比如抽血、耳鼻喉体检是排队的，而超声、外科、口腔等又是叫号的，不细心的小伙伴很容易踩坑。排队外科的时候，大家摆起很长的队伍，我误以为是排队的，于是就无聊地玩起了手机，可等快排到门口的时候，竟有人排在我后面先我一步被叫号进去，我内心气愤不已，大呼不公平，而且更离谱的事情，我在口腔科排队的时候，明明显示屏上排的是号，反而有个护士在协调让人排队来保证公平，可这对排号的人而言，明明屏幕上下一个就是自己了，却还是站着苦苦地排队等着，自己是按规则来的，可心里依旧觉得不公平。",-1),h=e("p",null,"体检中心规则的双标，一会排队一会排号，实在让人得花时间来琢磨，但一个人一年也就体检一会，这样的体检体验实在差强人意。",-1),u=[c,a,l,d,r,i,h];function p(f,m){return t(),s("div",null,u)}const B=_(n,[["render",p],["__file","2023-02-03-体验也是按原则做事.html.vue"]]);export{B as default};
