import{_ as n,X as o,Y as a,Z as e,$ as t,a1 as p,a0 as r,C as s}from"./framework-e45c54a7.js";const c={},l=r('<h1 id="放弃财务自由-自由自然而来" tabindex="-1"><a class="header-anchor" href="#放弃财务自由-自由自然而来" aria-hidden="true">#</a> 放弃财务自由，自由自然而来</h1><p>Created time: March 11, 2023 9:36 PM</p><p>点评：这篇文章立意是好的，能写出爆款来，不过案例不够充分，给人感觉比较鸡汤文，需要填充大量的案例来充实骨肉，应该能做到不错的阅读量。</p><p>&lt;放弃财务自由，自由自然而来&gt;</p><p>财务自由，被普遍认知的理解是“一个人的被动收入大于他的日常开销，如果可以，得再加上一个远大于”</p><p>我有个朋友对这个概念相当喜欢，甚至到了痴迷的程度。互联网新闻上爆出有人因为公司上市、炒股暴富、炒币暴富宣布提前退休过上自己喜欢的生活，是他最爱看的故事。</p><p>每一次造富神话的背后，我都能看到他兴奋的样子，仿佛口水都已经流下来了，然后经常会感叹一句：“那个人为什么不是我”。</p><p>很显然，人们在理解财务自由这件事情上，还隐含着一种幻想。</p><p>那就是假如我有足够多的钱，我就可以不再为眼下的目标发愁，买车，买房，好看的衣服包包全部扫入囊中，仿佛财富自由后可以肆意挥霍，无度消耗。</p><p>我并不劝人放弃这种幻想，因为现实会替我狠狠地在来上一下</p><p>一个人要想在至少二线城市生活，房子的成本恐怕就足够将绝大多数人打回现实，被判有期 15 年起，如果再生养个孩子，恐怕还得超级加倍</p><p>但绝大多数普通人，甚至不普通的人，都是由欲望元素构成的生活</p><p>产生欲望，通过付出得到资源，用资源换取欲望的满足</p><p>&lt;狂飙&gt;高启强从卖鱼翁到黑道头目</p>',14),i={href:"https://baike.baidu.com/item/%E4%B9%94%E4%B8%B9%C2%B7%E8%B4%9D%E5%B0%94%E7%A6%8F%E7%89%B9/6213369?fromModule=lemma_inlink",target:"_blank",rel:"noopener noreferrer"},d=e("p",null,"欲望填充的人生案例数不胜数",-1),h=e("p",null,"大多数人，财富每多一点，欲望就会涨一分",-1),m=e("p",null,"财务自由变成了遥不可及的目的，在追逐的过程中失去自由，因为追逐自由而失去自由，多讽刺的一件事儿",-1),u=e("p",null,"放弃财务自由，专注此刻，好好活着，从身边的小事寻找价值感，你不需要追逐，那种自由感自会环绕，自然而来",-1);function f(B,E){const _=s("ExternalLinkIcon");return o(),a("div",null,[l,e("p",null,[t("<华尔街之狼>"),e("a",i,[t("乔丹·贝尔福特"),p(_)]),t("从普通销售到纸醉金迷沉沦入狱")]),d,h,m,u])}const x=n(c,[["render",f],["__file","2023-03-02-放弃财务自由，自由自然而来.html.vue"]]);export{x as default};
